import{a as e,b as t,o as s,e as a}from"./entry.048925a3.js";const i={},c={class:"bg-pink-50 min-h-screen h-full p-10 font-mono text-pink-800 text-center"},r=a('<div class="w-9/12 mx-auto"><p class="text-5xl">Diversity in Trophic Structure</p><p class="text-md mt-2">Diversity and trophic structure are attributes of an ecosystem that play a role in how they act</p><p class="text-3xl mt-8">Species Diversity</p><p class="text-md mt-2">Species diversity is the variety of different organisms in an ecosystem; it can be hard to measure due to very rare organisms and microorganisms</p><p class="text-lg mt-1">Species Richness</p><p class="text-md mt-1">Species richness is the number of species there are</p><p class="text-lg mt-1">Relative Abundance</p><p class="text-md mt-1">Relative abundance is the percentage that each species takes up</p><p class="text-lg mt-1">Shannon Diversity Index</p><p class="text-md mt-1">H = - (Pa In Pa + Pb In Pb + Pc In Pc + ...)</p><p class="text-md mt-1">Where a, b, c are different species and Pa, Pb, Pc are the percentage of those species</p><p class="text-3xl mt-8">Diversity and Community Stability</p><p class="text-md mt-2">The more diverse an ecosystem is, the more stable it is and more resistant it is to <b>invasive predators</b></p></div>',1),n=[r];function p(o,m){return s(),t("div",c,n)}const l=e(i,[["render",p]]);export{l as default};
